public class Loop1 {
	
	void ForLoop1() {
		int index = 1;
		
		for(;index<=5;index++) {
			System.out.println("Chaitanya");
		}
		
		System.out.println("index :" + index);
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Loop1 loop1 = new Loop1();
		loop1.ForLoop1();
	}

}
