public class Assigment26_P3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Assigment26_P3 p3 = new Assigment26_P3();
		p3.swapNum();
	}

	void swapNum() {
		// TODO Auto-generated method stub
		int a=6;
		int b=17;
		int c = 0;
		System.out.println("a = "+a+"\nb = "+b);
		c=a;
		a=b;
		b=c;
		System.out.println("After Swapping :");
		System.out.println("a = "+a+"\nb = "+b);
		
	}

}
