package harshada.Assignment_53;

public class P1 {
	
	void print(){
		
		for(int i=1; i<=4; i++) {
			
			for(int j=0; j<i; j++) {
				
				System.out.print("*");
			}
			
			System.out.println();
		}
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// TODO Auto-generated method stub
		P1 p1=new P1();
		p1.print();
	}

}
